import styled from "styled-components";

export const CardWrapper = styled.div`
  box-shadow: 0px 0px 30px #00000026;
  width: 1280px;
  height: 370px;
  background: #ffffff 0% 0% no-repeat padding-box;
  border-radius: 10px;
  opacity: 1;
  font-size: 25px;
  overflow: hidden;

  position: relative;
  margin: 20px 20px;
  padding: 20px 20px;
  text-align: left;
  align-item:center;
`;
export const SmallWrapper = styled.img`
  box-shadow: 0px 0px 30px #00000026;
  width: 300px;
  height: 330px;
  border-radius: 10px;
  object-fit:cover;
`;
export const ForFlex = styled.div`
    display:flex;
`;

export const Box = styled.div`
    width:900px;
    height:330px;
    margin: 0 auto;
    text-align: center;
`;
export const Profile = styled.img`
  display: block;
  margin: 0 auto;
  width: 124px;
  height: 124px;
  color: black;
  border-radius: 100px;
  object-fit: cover;
`;

export const Name = styled.div`
  margin: 20px auto;
  text-align: center;
  align-items: center;
  display: inline-block;
  height: 20px;
  border-radius: 16px;
  font-size: 30px;
  padding: 2px 13px;
  color: black;
  font: normal normal bold 30px/35px Spoqa Han Sans Neo;
`;

export const Skil = styled.div`
  text-align: center;
  font: normal normal bold 20px/25px Spoqa Han Sans Neo;
  color: black;
  opacity: 1;
`;

export const Int = styled.div`
  text-align: center;
  font: normal normal bold 14px/18px Spoqa Han Sans Neo;
  color: black;
  opacity: 1;
`;

export const Tag = styled.div`
  margin-top: 30px;
  text-align: center;
  font: normal normal normal 13px/17px Spoqa Han Sans Neo;
  letter-spacing: 0px;
  color: #3f7ae2;
  opacity: 1;
`;

export const HeartWrapper = styled.img`
