import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styled from "styled-components";
import SimpleImageSlider from "react-simple-image-slider";
import "react-slideshow-image/dist/styles.css";

import {
  HeaderWrapper,
  Logo,
  Box,
  Wrapper,

} from "./ReservationStyle"
import Slider from "react-slick";
import Img1 from "../assets/banner/1.jpg"
import Img2 from "../assets/banner/2.jpg"
import Img3 from "../assets/banner/3.jpg"
import Img4 from "../assets/banner/4.jpg"
import { Hr } from "../base/CommonStyle";
import { ForFlex } from "../Movie/MovieCardStyle";

const Reservation = () => {

  const settings = {
    autoPlay: true,
  };

  const imgslide = [{
    url: Img1
  },{
    url: Img2
  },{
    url: Img3
  },{
    url: Img4
  }]

  const Bigregion=[{
    index:0,
    label:"서울",
  },{
    index:1,
    label:"경기/인천",
},{
    index:2,
    label:"충청/대전",
},{
    index:3,
    label:"전라/광주",
},{
    index:4,
    label:"경북/대구",
},{
    index:5,
    label:"경남/부산/울산",
},{
    index:6,
    label:"강원",
},{
    index:7,
    label:"제주",
}]

  const [select,SetSelect]=useState({
      movie: "",
      bigregion: 0,
      region: 0,
      time: 0,
  })
  const [subselect,setSubselect]=useState([""])

  const handleChange=()=>{
    if(select.bigregion==0){
        setSubselect(["가산디지털","강남","강동","건대입구","김포공항","노원","도곡","서울대입구","수유"]);
    }
    else if(select.bigregion==1){
        setSubselect(["광교아울렛","부평","송탄"]);
    }
    else if(select.bigregion==2){
        setSubselect(["당진","대전","아산터미널"]);
    }
    else if(select.bigregion==3){
        setSubselect(["광주","전주"]);
    }
    else if(select.bigregion==4){
        setSubselect(["경주","동성로"]);
    }
    else if(select.bigregion==5){
        setSubselect(["부산본점","통영"]);
    }
    else if(select.bigregion==6){
        setSubselect(["동해","속초"]);
    }
    else if(select.bigregion==7){
        setSubselect(["서귀포","제주아라"]);
    }

  }

  useEffect(()=>{handleChange();console.log(select)},[select]);

  return (
    <>
        <HeaderWrapper>
          <ForFlex>
          <Wrapper> 
            <Logo>Movie</Logo>
          </Wrapper>
          <Wrapper> 
            <Logo>Region</Logo>
            {Bigregion.map((item) => (
              <Box onClick={()=>{SetSelect((prev)=>{
                  return Object.assign({},prev,{bigregion: item.index})
              })}}>{item.label}</Box>
            ))}
          </Wrapper>
          <Wrapper> 
            <Logo>Theater</Logo>
            {subselect.map((item, index) => (
              <Box>{item}</Box>
            ))}
          </Wrapper>
          <Wrapper> 
            <Logo>Time</Logo>
          </Wrapper>
          </ForFlex>
        </HeaderWrapper>
    </>
  );
};

export default Reservation;
